import React, { useEffect } from "react";
import { connect } from "react-redux";
import { fetchTodos, addTodo } from "./actions/indexactions";
import TodoList from "./component/TodoList";
import AddTodo from "./component/AddTodo";
import "./styles.css";

const TodoApp = ({ todos, fetchTodos, addTodo }) => {
  useEffect(() => {
    fetchTodos(); // Fetch todos when the component mounts
  }, [fetchTodos]);

  return (
    <div className="todo-app">
      <h1 className="todo-title">Todo List</h1>
      <AddTodo addTodo={addTodo} />
      <TodoList todos={todos} />
    </div>
  );
};

// Mapping the redux state to props
const mapStateToProps = (state) => ({
  todos: state.data.todos || [], // Assuming the todos are stored inside `data.todos`
});

export default connect(mapStateToProps, { fetchTodos, addTodo })(TodoApp);
