import React, { useState } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { addTodo } from "../../actions";  // This is your Redux action

const AddTodo = ({ addTodo }) => {
  const [task, setTask] = useState("");

  const handleAdd = () => {
    if (task.trim()) {
      axios
        .post("http://localhost:3000/api/todo", { task })
        .then((res) => {
          console.log("Task Added", res.data);
          setTask(""); // Clear the input field
          addTodo(res.data); 
        })
        .catch((err) => {
          console.error("Error Adding Task", err);
        });
    } else {
      console.warn("Enter a Task");
    }
  };

  return (
    <div className="add-todo">
      <input
        type="text"
        value={task}
        className="add-todo-input"
        placeholder="Enter Task"
        onChange={(e) => setTask(e.target.value)}
      />
      <button type="button" className="add-todo-button" onClick={handleAdd}>
        +
      </button>
    </div>
  );
};

// Connect AddTodo to Redux
export default connect(null, { addTodo })(AddTodo);
