import { useState } from "react";
import { useDispatch } from "react-redux";
import { addTodo } from "../redux/todoSlice"; // Import the addTodo action
import axios from "axios";
import "./AddTodo.css";

const AddTodo = () => {
  const [task, setTask] = useState("");
  const dispatch = useDispatch(); // Get the dispatch function from Redux

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent form submission

    if (task.trim()) {
      const newTodo = { task }; // Prepare new todo object

      // Optimistically update Redux state by dispatching the addTodo action
      dispatch(addTodo(newTodo));

      // Send request to backend to add todo
      axios
        .post("http://localhost:9091/api/todo", { task })
        .then((res) => {
          console.log("Task Added", res.data);
        })
        .catch((err) => {
          console.error("Error Adding Task", err);
          // Optionally, you could dispatch another action to handle errors
        });

      setTask(""); // Clear input field after submitting
    } else {
      console.warn("Enter a Task");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="add-todo">
      <input
        type="text"
        value={task}
        className="add-todo-input"
        placeholder="Enter Task"
        onChange={(e) => setTask(e.target.value)}
      />
      <button type="submit" className="add-todo-button">
        +
      </button>
    </form>
  );
};

export default AddTodo;
