// AddTodo.js

import React, { useState } from "react";
import axios from "axios";
import "./AddTodo.css";

const AddTodo = ({ addTodo }) => {
  const [task, setTask] = useState("");

  const handleAdd = () => {
    if (task.trim()) {
      axios
        .post("http://localhost:3000/api/todo", { task })
        .then((res) => {
          console.log("Task Added", res.data);
          addTodo(task); // Pass the new task to the parent to add it
          setTask(""); // Clear the input field after adding
        })
        .catch((err) => {
          console.error("Error Adding Task", err);
        });
    } else {
      console.warn("Enter a Task");
    }
  };

  return (
    <form className="add-todo" onSubmit={(e) => e.preventDefault()}>
      <input
        type="text"
        value={task}
        className="add-todo-input"
        placeholder="Enter Task"
        onChange={(e) => setTask(e.target.value)}
      />
      <button type="button" className="add-todo-button" onClick={handleAdd}>
        +
      </button>
    </form>
  );
};

export default AddTodo;
