import React, { useState, useEffect } from "react";
import AddTodo from "./component/AddTodo/AddTodo";
import TodoList from "./component/TodoList/TodoList";
import axios from "axios";

export default function TodoApp() {
  const [todos, setTodos] = useState([]);

  // Fetch todos on initial load
  useEffect(() => {
    fetchTodos();
  }, []);

  // Fetch todos from the server
  const fetchTodos = () => {
    axios
      .get("http://localhost:3000/api/todo")
      .then((res) => setTodos(res.data))
      .catch((err) => console.error("Error fetching todos", err));
  };

  // Optimistically update the todos
  const addTodo = (newTask) => {
    // Optimistically update the UI by adding the task
    const newTodo = { task: newTask };

    setTodos((prevTodos) => [...prevTodos, newTodo]);

    // Send the new task to the server
    axios
      .post("http://localhost:3000/api/todo", { task: newTask })
      .then((res) => {
        console.log("Task added successfully:", res.data);
      })
      .catch((err) => {
        console.error("Error adding task:", err);
        // If adding to server fails, you can handle the error (e.g., remove the task from UI or show an error message)
      });
  };

  return (
    <div className="todo-app">
      <h1 className="todo-title">Todo List</h1>
      <AddTodo addTodo={addTodo} />
      <TodoList todos={todos} />
    </div>
  );
}
