import React, { useState, useEffect } from "react";
import axios from "axios";
import TodoList from "./component/TodoList/TodoList";
import AddTodo from "./component/AddTodo/AddTodo";
import "./styles.css";

export default function TodoApp() {
  const [todos, setTodos] = useState([]);

  // Fetch todos when the component mounts
  useEffect(() => {
    axios
      .get("http://localhost:9091/api/todo")  // Make sure this matches your backend URL
      .then((res) => {
        console.log("Fetched todos:", res.data.todos);  // Log the fetched todos
        setTodos(res.data.todos);  // Update state with fetched todos
      })
      .catch((err) => {
        console.error("Error fetching todos:", err);  // Log any errors
      });
  }, []);  // Empty dependency array means it runs once when the component mounts

  const addTodo = (newTodo) => {
    setTodos((prevTodos) => [...prevTodos, newTodo]);  // Add the new todo to state
  };

  return (
    <div className="todo-app">
      <h1 className="todo-title">Todo List</h1>
      <AddTodo addTodo={addTodo} />
      <TodoList todos={todos} />
    </div>
  );
}
